var loginRadiusReferralSubmit = null;
function loginRadiusCheckAll(lrform, check) {
      lrform = 'popupinner';
      jQuery('#' + lrform + ' :checkbox:enabled').prop('checked', check);
}

jQuery("#loginRadiusReferralSubmit").click(function () {       
    var lrValid = false;      
    var loginRadiusReferralSubmit = jQuery("#loginRadiusReferralSubmit").val();
    var loginRadiusIdentifier = jQuery("#loginRadiusIdentifier").val();
    var loginRadiusProvider = jQuery("#loginRadiusProvider").val();
    var loginRadiusContacts = [];
    
    jQuery("[name='loginRadiusContacts[]']").each(function(){
        if(jQuery(this).is(":checked")){
        loginRadiusContacts.push(jQuery(this).val());
        lrValid = true;
        }
    })    
     if (!lrValid) {      
          jQuery('.socialnoerror').hide();
          jQuery('.socialerror').show();  
        return false;
    }
  
    jQuery.ajax({
        type: 'POST', 
        url: "index.php?plugin=userregistration",
        dataType: 'json',
        data: {                   
            loginRadiusContacts: loginRadiusContacts,
            loginRadiusIdentifier: loginRadiusIdentifier,
            loginRadiusProvider: loginRadiusProvider,
            loginRadiusReferralSubmit: loginRadiusReferralSubmit,

        },
        success: function (response) {
            alert('A message has been successfully send to your contacts.');//           
            window.location.replace(response);        
        
        }
    });
 });
jQuery("#loginRadiusReferralSkip").click(function () {    
    var loginRadiusReferralSkip = jQuery("#loginRadiusReferralSkip").val();
    var loginRadiusIdentifier = jQuery("#loginRadiusIdentifier").val();
    var loginRadiusProvider = jQuery("#loginRadiusProvider").val();
    
  
    jQuery.ajax({
        type: 'POST', 
        url: "index.php?plugin=userregistration",
        dataType: 'json',
        data: {        
            loginRadiusIdentifier: loginRadiusIdentifier,
            loginRadiusProvider: loginRadiusProvider,
            loginRadiusReferralSkip: loginRadiusReferralSkip,
        },
        success: function (response) {   
              window.location.replace(response);            
        }
    });
 });
